package Switch;

public class KIs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k = 65;
		switch (k){
		
		default :
			System.out.println("Website");
			
		case 65 :
			System.out.println("Merit");
			
		case 'k':
			System.out.println("Campus");
			
		case 'j' :
			System.out.println("Java");
			break;
			
			
		}

	}

}
