package Whileloop;

public class Table2To10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		  int num = 9, i = 1;
	        while(i <= 10)
	        {
	            System.out.println ("%d * %d = %d \n", num, i, num * i);
	            i++;
	        }
	    }
	
	}


